#function to make some values empty
def empty(df, percent):
    percent = int(percent)/100
    df1 = df.sample(frac= percent)
    df1.iloc[:, 0] ='NaN'
    df2 = df
    df2.rename(columns={list(df2)[0]:'A'}, inplace=True)
    df1.rename(columns={list(df1)[0]:'B'}, inplace=True)
    df3 = df2.join(df1)
    df3['result'] = df3['B'].fillna(df3['A'])
    return (df3[['result']])

#function to write xml file
def to_xml(df, filename=None, mode='w'):
    def row_to_xml(row):
        xml = ['<item>']
        for i, col_name in enumerate(row.index):
            xml.append('  <field name="{0}">{1}</field>'.format(col_name, row.iloc[i]))
        xml.append('</item>')
        return '\n'.join(xml)
    res = '\n'.join(df.apply(row_to_xml, axis=1))

    if filename is None:
        return str(res)
    with open(filename, mode) as f:
        f.write(str(res.encode('utf8')))

